// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, NavLink } from 'react-router-dom';
import CovidStats from './components/CovidStats';
import logo from './components/logo/logo.png';
import Footer from './components/Footer'; // Ajusta la ruta según tu estructura
import './App.css';

function App() {
  return (
    <Router>
      <div className="app-container">
        <header className="app-header">
          <img src={logo} alt="ViRix logo" className="logo" />
          <div className="slogan">
            <p>Spot the risk, stay safe</p>
          </div>
          <nav className="nav-bar">
            <NavLink to="/" exact>Map</NavLink>
            <NavLink to="/info">Info</NavLink>
            <NavLink to="/about">About Us</NavLink>
            <NavLink to="/tips">Tips</NavLink>
          </nav>
        </header>
        <main>
          <Routes>
            <Route path="/" element={<CovidStats />} />
            {/* Add additional routes for Info, About Us, and Tips pages */}
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
