import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import CovidStats from './components/CovidStats';
import { slide as Menu } from 'react-burger-menu';
import logo from './components/logo/logo.png';
import Footer from './components/Footer';
import Info from './components/Info';
import Tips from './components/Tips';
import AboutUs from './components/AboutUs';
import './App.css';

function App() {
    const [menuOpen, setMenuOpen] = useState(false);

    const handleMenuClick = () => {
        setMenuOpen(!menuOpen);
    };

    const closeMenu = () => {
        setMenuOpen(false);
    };

    return (
        <Router>
            <div className="app-container">
                <header className="app-header">
                    <img src={logo} alt="ViRix Logo" className="logo" />
                    <Menu isOpen={menuOpen} onStateChange={(state) => setMenuOpen(state.isOpen)}>
                        <Link to="/" onClick={closeMenu}>Map</Link>
                        <Link to="/info" onClick={closeMenu}>Info</Link>
                        <Link to="/about" onClick={closeMenu}>About Us</Link>
                        <Link to="/tips" onClick={closeMenu}>Tips</Link>
                    </Menu>
                </header>
                <main>
                    <Routes>
                        <Route path="/" element={<CovidStats />} />
                        <Route path="/info" element={<Info />} />
                        <Route path="/tips" element={<Tips />} />
                        <Route path="/about" element={<AboutUs />} />
                    </Routes>
                </main>
                <Footer />
            </div>
        </Router>
    );
}

export default App;
