// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import CovidStats from './components/CovidStats';
import logo from './components/logo/logo.png';
import Footer from './components/Footer'; // Ajusta la ruta según tu estructura
import Info from './components/Info';
import Tips from './components/Tips';
import './App.css';

function App() {
  return (
      <Router>
          <div className="app-container">
              <header className="app-header">
                  <img src={logo} className="logo" alt="ViRix Logo" />
                  <h1>ViRix</h1>
                  <nav className="nav-bar">
                      <Link to="/">Map</Link>
                      <Link to="/info">Info</Link>
                      <Link to="/tips">Tips</Link> {/* Add Link for Tips */}
                  </nav>
              </header>
              <main>
                  <Switch>
                      <Route exact path="/" component={CovidStats} />
                      <Route path="/info" component={Info} />
                      <Route path="/tips" component={Tips} /> {/* Add Route for Tips */}
                  </Switch>
              </main>
          </div>
      </Router>
  );
}

export default App;
