// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import CovidStats from './components/CovidStats';
import { slide as Menu } from 'react-burger-menu';
import logo from './components/logo/logo.png';
import Footer from './components/Footer'; // Ajusta la ruta según tu estructura
import Info from './components/Info';
import Tips from './components/Tips';
import AboutUs from './components/AboutUs';
import './App.css';

function App() {
  return (
    <Router>
      <div className="app-container">
        <header className="app-header">
          <div className="header-content">
            <img src={logo} alt="ViRix Logo" className="logo" />
            <Menu right styles={{ bmItem: { color: 'white' }, bmMenu: { background: '#4348be' } }}>
              <Link to="/">Map</Link>
              <Link to="/info">Info</Link>
              <Link to="/about">About Us</Link>
              <Link to="/tips">Tips</Link>
            </Menu>
          </div>
          <div className="slogan">
            <p className="slogan-text">Spot the risk, stay safe</p>
          </div>
        </header>
        <main>
          <Routes>
            <Route path="/map" element={<CovidStats />} />
            <Route path="/" element={<CovidStats />} />
            <Route path="/info" element={<Info />} />
            <Route path="/tips" element={<Tips />} />
            <Route path="/about" element={<AboutUs />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
