// App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom'; // Use Routes instead of Switch
import CovidStats from './components/CovidStats';
import Info from './components/Info';
import Tips from './components/Tips'; // Import Tips component
import './App.css';

function App() {
    return (
        <Router>
            <div className="app-container">
                <header className="app-header">
                    <img src={logo} className="logo" alt="ViRix Logo" />
                    <h1>ViRix</h1>
                    <nav className="nav-bar">
                        <Link to="/">Map</Link>
                        <Link to="/info">Info</Link>
                        <Link to="/tips">Tips</Link> {/* Add Link for Tips */}
                    </nav>
                </header>
                <main>
                    <Routes> {/* Replace Switch with Routes */}
                        <Route path="/" element={<CovidStats />} />
                        <Route path="/info" element={<Info />} />
                        <Route path="/tips" element={<Tips />} /> {/* Add Route for Tips */}
                    </Routes>
                </main>
            </div>
        </Router>
    );
}

export default App;
